  constructor() {
    this.configuration = {
      time: 60,
      maxWordsInRow: 10,
      attempts: -1,
      language: 'en',
    };
  }

  ngOnInit(): void {
    this.generateTest();
  }

  public observeInput(event: KeyboardEvent) {
    if (!this.inProgress && !this.isFinished) {
      this.startTimer();
      this.inProgress = true;
    }

    if (String.fromCharCode(event.keyCode) == ' ') {
      this.wordEntered();
    }
  }

  public restart() {
    this.remainingTime = this.configuration.time;
    this.isFinished = false;
    this.wordInput = '';
    this.generateTest();
  }

  private startTimer() {
    const clock = setInterval(() => {
      this.remainingTime--;
      if (this.remainingTime == 0) {
        clearInterval(clock);
        this.inProgress = false;
        this.isFinished = true;
        this.finished.emit(this.result);

        if (this.configuration.attempts > 0) {
          this.configuration.attempts--;
        }
      }
    }, 1000);
  }

  private generateTest() {
    this.remainingWords = [];
    this.writtenWords = [];
    this.result.charsTyped = 0;
    this.result.wordsCorrect = 0;
    this.result.wordsSubmitted = 0;
    this.remainingWords.push(...this.shuffleWords());
  }

  private wordEntered() {
    if (!this.inProgress) {
      return;
    }

    this.result.wordsSubmitted++;
    this.result.charsTyped += this.wordInput.trim().length;

    if (this.wordInput.trim() === this.remainingWords.slice(0, 1)[0]) {
      this.result.wordsCorrect++;
      this.writtenWords.push({word: this.remainingWords.slice(0, 1)[0], isCorrect: true})
    } else {
      this.writtenWords.push({word: this.remainingWords.slice(0, 1)[0], isCorrect: false})
    }
    this.remainingWords.splice(0, 1);

    if (this.writtenWords.length == this.configuration.maxWordsInRow) {
      this.writtenWords = [];
    }

    this.wordInput = '';
  }

  private shuffleWords(): string[] {
    const returnArray = [];
    let array;
    switch (this.configuration.language) {
      case 'de':
        array = this.langWords.de;
        break;
      case 'nl':
        array = this.langWords.nl;
        break;
      case 'fr':
        array = this.langWords.fr;
        break;
      case 'es':
        array = this.langWords.es;
        break;
      case 'ca':
        array = this.langWords.ca;
        break;
      case 'it':
        array = this.langWords.it;
        break;
      case 'pt':
        array = this.langWords.pt;
        break;
      case 'th':
        array = this.langWords.th;
        break;
      case 'ja':
        array = this.langWords.ja;
        break;
      default:
        array = this.langWords.en;
    }

    for (let i = 0; i < array.length; i++) {
      let randInt = Math.floor(Math.random() * array.length);
      returnArray.push(array[randInt]);
    }

    return returnArray;
  }
